import React, { Fragment, useEffect, useState } from "react";
import './UpdateServiceStyle.css'
import { Link } from 'react-router-dom'
import apiBaseUrl from "../shared/utils/Api";
import ForbidenComponent from "../shared/components/fordiben/ForbidenComponent";
import { useAuth0 } from "@auth0/auth0-react";
import { Redirect } from 'react-router';





function UpdateServicePage() {
    const [services, setServices] = useState([]);
    const [validUser, setValidUser] = useState(false);
    const { user, isAuthenticated } = useAuth0();
    const [Valor, setValor] = useState([]);



    function deleteElement(id) {
        deleteService(id);
        console.log(id);
    }
    function setValor1(value, id) {
        setValor(value.target.value);
        console.log(value.target.value);
        console.log(id);
        uppdateValor(id, value.target.value);

    }
    function changeButton() {
        const button = "Guardar"
        return

    }
    const [service, setService] = useState({
       
    })

    const getService = async () => {
        try {
            const response = await fetch("http://localhost:3001/get-Service");
            const jsonResponse = await response.json();
            const responseServices = jsonResponse.data;
            const listservices = responseServices.map((service) =>
                <tr>
                    <th scope="row">{service.id}</th>
                    <td>{service.detalle}</td>
                    <td><input name="valor" type="number" class="form-control pesos" id="inputNombre" placeholder={service.valor} /></td>
                    <td><input name="valor" type="name" class="form-control pesos" id="inputNombre" placeholder={service.estado} /></td>
                    <td>
                        <div className="mb-3">
                            {validUser == true ? <button onClick={(id) => changeButton()} type="button" class="btn btn-dark">Guardar</button> : <ForbidenComponent />}
                            {validUser == true ? <button onClick={(id) => deleteElement(service.id)} type="button" class="btn btn-dark bot">{" "}
                                Borrar{" "}</button> : <ForbidenComponent />}
                        </div>
                    </td>
                    <div className="mb-3">
                        <td>

                        </td>
                    </div>
                </tr>
            );
            setServices(listservices)
        }
        catch (error) {
            console.log(error)
        }
        
    
        }

    }
    
    const validateUserRole = async () => {
        const response = await fetch(`http://localhost:3001/get-user?email=${user.email}`);
        const jsonResponse = await response.json();
        return jsonResponse;
    }

    const grantAccess = async () => {
        let userData;
        if (isAuthenticated) {
            userData = await validateUserRole();
        }
        else {
            setValidUser(false);
            return;
        }
        if (userData) {
            if (userData.role != 'Invited') {
                setValidUser(true);
                localStorage.setItem("state", true);
                await getService();

            }
            else {
                setValidUser(false);
            }
        }
        else {
            setValidUser(false);
        }
    }
    const deleteService = async (id) => {
        const productData = {
            id: id,
        };


        const response = await fetch(`http://localhost:3001/delete-service`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(productData),
        });
        const jsonResponse = await response.json();
        console.log(jsonResponse);
    };
    const uppdateValor = async (id, valor) => {
        const productData = {
            id: id,
            valor: valor,
        };
        const response = await fetch(`http://localhost:3001/update-user-role`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(productData),
        });
        const jsonResponse = await response.json();
        console.log(jsonResponse);
    };
    const handleChange = e => {
        setServices({
            ...services,
            [e.target.name]: e.target.value
        })
    }
    let {  valor, estado } = service
    const handleSubmit = () => {
        if (valor == '' || estado == '') {
            alert('Todos los campos deben estar rellenados')
            return
        }
        const requestInit = {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(service)
        }


        fetch('http://localhost:3001/update-service', requestInit)
            .then(res => res.json())
            .then(res => console.log(res))



    useEffect(() => {
        grantAccess();
        getService();
    }, [isAuthenticated, validUser]);

    return (
        <Fragment>
        <div onSubmit={handleSubmit}>     
            <hr class="linea"></hr>

            <h2 class="texti">EDITAR SERVICIOS</h2>
            <div className="row">
                <div className="col ">
                    <a class="texti0">SERVICIOS</a>


                </div>
            </div>
            <hr class="linea line0"></hr>
            <div class="row row0">
                <div class="col">
                    {validUser == true ? <input type="text" class="form-control inpu" placeholder="# Servicio" aria-label="Search" /> : <ForbidenComponent />}
                </div>
                <div class="col">
                    {validUser == true ? <button type="button" class="btn btn-dark but1">Buscar</button> : <ForbidenComponent />}
                </div>
            </div>
            <table class="table row1">
                <thead class="table-dark ">
                    <tr>
                        <th scope="col">Id </th>
                        <th scope="col">Servicio</th>
                        <th scope="col">Valor</th>
                        <th scope="col">Estado</th>
                        <th scope="col">Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {services}
                </tbody>
            </table>
            <hr class="linea line1"></hr>

            {/* <!-- Button trigger modal --> */}


</div>
        </Fragment>

    )


}
export default UpdateServicePage;
